import React, { useState, useEffect } from "react";
import styled from "styled-components";

const API_KEY = "YOUR_GEMINI_API_KEY"; // Replace with your actual API key
const GEMINI_API_URL = `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent?key=${API_KEY}`;

const ChatContainer = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  z-index: 1000;
`;

const ChatButton = styled.button`
  background: #007bff;
  color: white;
  border: none;
  padding: 12px;
  font-size: 20px;
  border-radius: 50%;
  cursor: pointer;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
`;

const ChatWindow = styled.div`
  width: 300px;
  height: 400px;
  background: white;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: ${(props) => (props.visible ? "flex" : "none")};
  flex-direction: column;
  overflow: hidden;
  position: fixed;
  bottom: 80px;
  right: 20px;
`;

const ChatHeader = styled.div`
  background: #007bff;
  color: white;
  padding: 10px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: white;
  font-size: 18px;
  cursor: pointer;
`;

const ChatBody = styled.div`
  flex: 1;
  padding: 10px;
  overflow-y: auto;
  font-size: 14px;
  background: #f9f9f9;
`;

const ChatInputContainer = styled.div`
  display: flex;
  border-top: 1px solid #ddd;
  padding: 5px;
  background: #fff;
`;

const ChatInput = styled.input`
  flex: 1;
  padding: 8px;
  border: none;
  outline: none;
`;

const SendButton = styled.button`
  background: #007bff;
  color: white;
  border: none;
  padding: 8px;
  cursor: pointer;
`;

const Chatbot = () => {
  const [visible, setVisible] = useState(false);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const storedMessages = JSON.parse(localStorage.getItem("chatHistory")) || [];
    setMessages(storedMessages);
  }, []);

  useEffect(() => {
    localStorage.setItem("chatHistory", JSON.stringify(messages));
  }, [messages]);

  const handleSendMessage = async (event) => {
    if (event.key === "Enter" || event.type === "click") {
      const input = event.target.value || document.getElementById("chat-input").value;
      if (!input.trim()) return;

      const userMessage = { text: input, sender: "user" };
      setMessages([...messages, userMessage]);

      const botResponse = await getBotResponse(input);
      setMessages((prev) => [...prev, { text: botResponse, sender: "bot" }]);

      document.getElementById("chat-input").value = "";
    }
  };

  const getBotResponse = async (input) => {
    try {
      const response = await fetch(GEMINI_API_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          contents: [
            {
              parts: [{ text: input }],
            },
          ],
        }),
      });

      const data = await response.json();
      return data?.candidates?.[0]?.content?.parts?.[0]?.text || "I'm not sure, but I'm learning!";
    } catch (error) {
      console.error("Error fetching response:", error);
      return "Oops! Something went wrong.";
    }
  };

  return (
    <ChatContainer>
      <ChatButton onClick={() => setVisible(!visible)}>ðŸ’¬</ChatButton>
      <ChatWindow visible={visible}>
        <ChatHeader>
          <span>Chatbot</span>
          <CloseButton onClick={() => setVisible(false)}>&times;</CloseButton>
        </ChatHeader>
        <ChatBody>
          {messages.map((msg, index) => (
            <div key={index} style={{ textAlign: msg.sender === "user" ? "right" : "left" }}>
              <p
                style={{
                  margin: "5px 0",
                  padding: "5px",
                  background: msg.sender === "user" ? "#007bff" : "#ddd",
                  color: msg.sender === "user" ? "white" : "black",
                  borderRadius: "5px",
                  display: "inline-block",
                }}
              >
                {msg.text}
              </p>
            </div>
          ))}
        </ChatBody>
        <ChatInputContainer>
          <ChatInput id="chat-input" type="text" placeholder="Type a message..." onKeyPress={handleSendMessage} />
          <SendButton onClick={handleSendMessage}>âž¤</SendButton>
        </ChatInputContainer>
      </ChatWindow>
    </ChatContainer>
  );
};

export default Chatbot;
